mapper.select:
SELECT h.*,hw.name as homeworkName,hw.descrip,f.name as file,f.id fileId,f.file_name fileName,u.username userName FROM homeworkAnswer h left join homework hw on h.homeworkId=hw.id left join t_file_record f on h.fileId=f.id left join t_user u on h.userId=u.id WHERE 1=1
and h.id=#{id}
and h.name like CONCAT('%',#{name},'%')
and h.homeworkId=#{homeworkId}
and h.userId=#{userId}
order by postTime

SELECT h.*,hw.name as homeworkName,hw.descrip,f.name as file,f.id fileId,f.file_name fileName,u.username userName FROM homeworkAnswer h left join homework hw on h.homeworkId=hw.id left join t_file_record f on h.fileId=f.id left join t_user u on h.userId=u.id WHERE 1=1
and h.id=#{id}
and h.name like CONCAT('%',#{name},'%')
and h.homeworkId=#{homeworkId}
and hw.userId=#{userId}
order by postTime
